import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageSquare, 
  TrendingUp, 
  Shield, 
  Settings, 
  Menu, 
  X, 
  Send, 
  FileText, 
  PieChart,
  ChevronRight,
  Bell,
  Search,
  Plus,
  User,
  UserCircle,
  Briefcase,
  Scale,
  PiggyBank,
  Target,
  Euro,
  Loader2,
  Upload,
  File,
  CheckCircle2,
  AlertCircle
} from 'lucide-react';
import { Link } from 'react-router-dom';
import { supabase, UserProfile } from '../lib/supabase';

interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
}

export function Dashboard() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [question, setQuestion] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState<ChatMessage[]>([
    {
      role: 'assistant',
      content: `Bonjour ! Je suis Francis, votre conseiller fiscal propulsÃ© par intelligence artificielle. ðŸ¤–

Je peux vous aider Ã  :
â€¢ Comprendre votre situation fiscale
â€¢ Optimiser vos impÃ´ts
â€¢ Analyser vos documents fiscaux
â€¢ RÃ©pondre Ã  vos questions sur la fiscalitÃ©

Pour commencer, vous pouvez :
1. Me poser une question sur la fiscalitÃ©
2. Partager un document fiscal pour analyse
3. Me demander des conseils personnalisÃ©s

Je m'adapte Ã  votre niveau de connaissance et vous guide pas Ã  pas. N'hÃ©sitez pas Ã  me poser toutes vos questions ! ðŸ’¡`
    }
  ]);
  const [activeTab, setActiveTab] = useState('home');
  const [profileData, setProfileData] = useState<Partial<UserProfile>>({
    situation: '',
    revenus: '',
    patrimoine: '',
    objectifs: [],
    toleranceRisque: '',
    horizonInvestissement: '',
    nombreEnfants: 0,
    agesEnfants: '',
    typeRevenus: [],
    autresRevenus: '',
    situationProfessionnelle: '',
    statutFiscal: '',
    regimeImposition: '',
    investissementsExistants: [],
    projetsImmobiliers: '',
    besoinsRetraite: '',
    situationFamiliale: '',
    localisation: '',
    zoneFiscale: '',
    secteurActivite: '',
    revenusPassifs: '',
    dettes: '',
    objectifsFinanciers: [],
    contraintesFiscales: [],
    compositionPatrimoine: []
  });

  const [profileCompleted, setProfileCompleted] = useState(false);
  const [chatMode, setChatMode] = useState('libre');
  const [userId, setUserId] = useState<string | null>(null);
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectedBanks, setConnectedBanks] = useState<string[]>([]);
  const [bankData, setBankData] = useState<any>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState<Array<{
    name: string;
    type: string;
    status: 'processing' | 'success' | 'error';
    data?: any;
  }>>([]);
  const [dragActive, setDragActive] = useState(false);

  const calculateProgress = () => {
    const requiredFields = [
      'situationFamiliale',
      'nombreEnfants',
      'localisation',
      'zoneFiscale',
      'situationProfessionnelle',
      'secteurActivite',
      'regimeImposition',
      'statutFiscal',
      'revenus',
      'typeRevenus',
      'patrimoine',
      'compositionPatrimoine',
      'investissementsExistants',
      'projetsImmobiliers',
      'besoinsRetraite',
      'horizonInvestissement',
      'toleranceRisque',
      'objectifs',
      'contraintesFiscales'
    ];

    const filledFields = requiredFields.filter(field => {
      const value = profileData[field as keyof typeof profileData];
      if (Array.isArray(value)) {
        return value.length > 0;
      }
      return value !== undefined && value !== '';
    });

    return Math.round((filledFields.length / requiredFields.length) * 100);
  };

  // VÃ©rifier l'authentification et charger le profil au chargement
  useEffect(() => {
    const checkUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUserId(user.id);
        await loadUserProfile(user.id);
      }
    };
    checkUser();
  }, []);

  // Charger le profil utilisateur depuis Supabase
  const loadUserProfile = async (userId: string) => {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error) throw error;

      if (data) {
        setProfileData(data);
        setProfileCompleted(true);
        // Charger l'historique des interactions
        if (data.interaction_history) {
          const history = data.interaction_history.map((interaction: { question: string; response: string }) => ({
            role: 'assistant',
            content: `Q: ${interaction.question}\nR: ${interaction.response}`
          }));
          setChatHistory(history);
        }
      }
    } catch (error) {
      console.error('Erreur lors du chargement du profil:', error);
    }
  };

  // Sauvegarder le profil utilisateur dans Supabase
  const saveUserProfile = async (profileData: Partial<UserProfile>) => {
    if (!userId) return;

    try {
      const { error } = await supabase
        .from('user_profiles')
        .upsert({
          user_id: userId,
          ...profileData,
          updated_at: new Date().toISOString()
        });

      if (error) throw error;
    } catch (error) {
      console.error('Erreur lors de la sauvegarde du profil:', error);
    }
  };

  // Mettre Ã  jour le profil avec les insights de la conversation
  const updateProfileWithInsights = async (question: string, response: string, insights: Array<{ type: string, value: string, confidence: number }>) => {
    if (!userId) return;

    try {
      const { data: currentProfile } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (!currentProfile) return;

      // Mettre Ã  jour le profil avec les nouveaux insights
      const updatedProfile = { ...currentProfile };
      insights.forEach(insight => {
        if (insight.confidence > 0.8) { // Seuil de confiance pour la mise Ã  jour
          updatedProfile[insight.type as keyof UserProfile] = insight.value;
        }
      });

      // Ajouter l'interaction Ã  l'historique
      const newInteraction = {
        role: 'assistant',
        content: `Q: ${question}\nR: ${response}`
      };

      updatedProfile.interaction_history = [
        ...(currentProfile.interaction_history || []),
        newInteraction
      ].slice(-50); // Garder les 50 derniÃ¨res interactions

      // Sauvegarder les modifications
      const { error } = await supabase
        .from('user_profiles')
        .update(updatedProfile)
        .eq('user_id', userId);

      if (error) throw error;

      // Mettre Ã  jour l'Ã©tat local
      setProfileData(updatedProfile);
    } catch (error) {
      console.error('Erreur lors de la mise Ã  jour du profil:', error);
    }
  };

  const handleProfileSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!userId) return;

    setIsLoading(true);
    try {
      await saveUserProfile(profileData);
      setProfileCompleted(true);

      const { recommendations, opportunities } = analyzeProfile();
      const welcomeMessage = `Bonjour ! Je suis Francis, votre conseiller fiscal propulsÃ© par intelligence artificielle. \n\nBasÃ© sur votre profil, je peux vous aider Ã  optimiser votre fiscalitÃ©. Voici mes premiÃ¨res recommandations :\n\n${recommendations.map(rec => `â€¢ ${rec}`).join('\n')}\n\nOpportunitÃ©s identifiÃ©es :\n${opportunities.map(opp => `â€¢ ${opp}`).join('\n')}\n\nPosez-moi des questions spÃ©cifiques pour en savoir plus sur ces recommandations.`;

      setChatHistory(prev => [...prev, { role: 'assistant', content: welcomeMessage }]);
      setActiveTab('chat');
    } catch (error) {
      console.error('Erreur lors de la sauvegarde du profil:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!question.trim() || !userId) return;

    setIsLoading(true);
    const userQuestion = question;
    setQuestion('');

    setChatHistory(prev => [...prev, { role: 'user', content: userQuestion }]);

    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: userQuestion,
          profile: profileData,
          chatMode: chatMode,
          userId: userId,
          context: chatHistory.slice(-5).map(msg => ({
            role: msg.role,
            content: msg.content
          }))
        }),
      });

      if (!response.ok) {
        throw new Error('Erreur de connexion avec Francis');
      }

      const data = await response.json();
      setChatHistory(prev => [...prev, { role: 'assistant', content: data.response }]);

      // Mettre Ã  jour le profil avec les insights de la rÃ©ponse
      if (data.insights) {
        await updateProfileWithInsights(userQuestion, data.response, data.insights);
      }
    } catch (error) {
      console.error('Erreur:', error);
      setChatHistory(prev => [...prev, { 
        role: 'assistant', 
        content: "DÃ©solÃ©, je rencontre des difficultÃ©s techniques. Veuillez rÃ©essayer dans quelques instants." 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Fonction pour gÃ©rer le drag & drop
  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  // Fonction pour gÃ©rer le drop
  const handleDrop = async (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      await handleFileUpload(e.dataTransfer.files[0]);
    }
  };

  // Fonction pour gÃ©rer le clic sur le bouton de tÃ©lÃ©chargement
  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      await handleFileUpload(e.target.files[0]);
    }
  };

  // Fonction pour traiter le fichier
  const handleFileUpload = async (file: File) => {
    setIsUploading(true);
    const newFile = {
      name: file.name,
      type: file.type,
      status: 'processing' as const
    };

    setUploadedFiles(prev => [...prev, newFile]);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('userId', userId || '');

      const response = await fetch('http://localhost:8000/process-document', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) throw new Error('Erreur lors du traitement du document');

      const data = await response.json();

      setUploadedFiles(prev => prev.map(f => 
        f.name === file.name 
          ? { ...f, status: 'success', data: data }
          : f
      ));

      // Ajouter un message dans le chat avec les informations extraites
      const chatMessage = `J'ai analysÃ© votre document "${file.name}". Voici les informations importantes :\n\n` +
        Object.entries(data)
          .map(([key, value]) => `â€¢ ${key}: ${value}`)
          .join('\n');

      setChatHistory(prev => [...prev, { role: 'assistant', content: chatMessage }]);
      setActiveTab('chat');

    } catch (error) {
      console.error('Erreur OCR:', error);
      setUploadedFiles(prev => prev.map(f => 
        f.name === file.name 
          ? { ...f, status: 'error' }
          : f
      ));
    } finally {
      setIsUploading(false);
    }
  };

  const analyzeProfile = () => {
    const { situation, revenus, patrimoine, objectifs, toleranceRisque, horizonInvestissement } = profileData;

    let recommendations = [];
    let opportunities = [];

    // Analyse de la situation familiale
    if (situation === 'marie' || situation === 'pacs') {
      recommendations.push("Optimisation du quotient familial");
      opportunities.push("DÃ©claration commune pour optimiser les tranches d'imposition");
    }

    // Analyse des revenus
    if (revenus === '50k-100k') {
      recommendations.push("Optimisation de la TMI avec le PER");
      opportunities.push("DÃ©fiscalisation via l'investissement locatif");
    } else if (revenus === '100k-200k') {
      recommendations.push("Mise en place d'une holding");
      opportunities.push("Optimisation IS/IR");
    } else if (revenus === '>200k') {
      recommendations.push("StratÃ©gie de dÃ©fiscalisation globale");
      opportunities.push("Optimisation des niches fiscales");
    }

    // Analyse du patrimoine
    if (patrimoine === '100k-300k') {
      recommendations.push("Diversification patrimoniale");
      opportunities.push("Investissement en SCPI");
    } else if (patrimoine === '300k-500k') {
      recommendations.push("StratÃ©gie de transmission anticipÃ©e");
      opportunities.push("Optimisation des droits de succession");
    } else if (patrimoine === '500k-1M' || patrimoine === '>1M') {
      recommendations.push("StratÃ©gie patrimoniale globale");
      opportunities.push("Optimisation fiscale multi-supports");
    }

    // Analyse des objectifs
    if (objectifs?.includes('retraite')) {
      recommendations.push("PER et assurance vie");
      opportunities.push("DÃ©fiscalisation retraite");
    } else if (objectifs?.includes('patrimoine')) {
      recommendations.push("Donation progressive");
      opportunities.push("Optimisation des abattements");
    } else if (objectifs?.includes('investissement')) {
      recommendations.push("Mix d'investissements dÃ©fiscalisÃ©s");
      opportunities.push("Optimisation des plus-values");
    } else if (objectifs?.includes('protection')) {
      recommendations.push("Assurance dÃ©cÃ¨s et invaliditÃ©");
      opportunities.push("Protection du conjoint et des enfants");
    }

    // Analyse du profil de risque
    if (toleranceRisque === 'conservateur') {
      recommendations.push("Investissements dÃ©fiscalisÃ©s sÃ©curisÃ©s");
      opportunities.push("SCPI de rendement");
    } else if (toleranceRisque === 'modere') {
      recommendations.push("Mix d'investissements Ã©quilibrÃ©");
      opportunities.push("Diversification multi-supports");
    } else if (toleranceRisque === 'dynamique') {
      recommendations.push("Investissements Ã  fort potentiel");
      opportunities.push("Optimisation des plus-values");
    }

    // Analyse de l'horizon d'investissement
    if (horizonInvestissement === 'court') {
      recommendations.push("Investissements liquides");
      opportunities.push("Optimisation court terme");
    } else if (horizonInvestissement === 'moyen') {
      recommendations.push("Investissements mixtes");
      opportunities.push("Diversification temporelle");
    } else if (horizonInvestissement === 'long') {
      recommendations.push("Investissements de long terme");
      opportunities.push("Optimisation transmission");
    }

    return { recommendations, opportunities };
  };

