          const bankResponse = await fetch('http://localhost:8000/bank-data', {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${accessToken}`,
            },
          });

          if (!bankResponse.ok) throw new Error('Erreur lors de la rÃ©cupÃ©ration des donnÃ©es');

          const data = await bankResponse.json();
          setBankData(data);
          setConnectedBanks(prev => [...prev, bankName]);

          authWindow?.close();
