import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageSquare, 
  TrendingUp, 
  Shield, 
  Settings, 
  Menu, 
  X, 
  Send, 
  FileText, 
  PieChart,
  ChevronRight,
  Bell,
  Search,
  Plus,
  User,
  UserCircle,
  Briefcase,
  Scale,
  PiggyBank,
  Target
} from 'lucide-react';
import { Link } from 'react-router-dom';
import { supabase, UserProfile } from '../lib/supabase';

export function Dashboard() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [question, setQuestion] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState<Array<{ type: 'user' | 'assistant', content: string }>>([]);
  const [activeTab, setActiveTab] = useState('home');
  const [profileData, setProfileData] = useState<Partial<UserProfile>>({
    situation: '',
    revenus: '',
    patrimoine: '',
    objectifs: [],
    toleranceRisque: '',
    horizonInvestissement: '',
    nombreEnfants: 0,
    agesEnfants: '',
    typeRevenus: [],
    autresRevenus: '',
    situationProfessionnelle: '',
    statutFiscal: '',
    regimeImposition: '',
    investissementsExistants: [],
    projetsImmobiliers: '',
    besoinsRetraite: '',
    situationFamiliale: '',
    localisation: '',
    zoneFiscale: '',
    secteurActivite: '',
    revenusPassifs: '',
    dettes: '',
    objectifsFinanciers: [],
    contraintesFiscales: [],
    compositionPatrimoine: []
  });

  const [profileCompleted, setProfileCompleted] = useState(false);
  const [chatMode, setChatMode] = useState('libre');
  const [userId, setUserId] = useState<string | null>(null);

  const calculateProgress = () => {
    const requiredFields = [
      'situationFamiliale',
      'nombreEnfants',
      'localisation',
      'zoneFiscale',
      'situationProfessionnelle',
      'secteurActivite',
      'regimeImposition',
      'statutFiscal',
      'revenus',
      'typeRevenus',
      'patrimoine',
      'compositionPatrimoine',
      'investissementsExistants',
      'projetsImmobiliers',
      'besoinsRetraite',
      'horizonInvestissement',
      'toleranceRisque',
      'objectifs',
      'contraintesFiscales'
    ];

    const filledFields = requiredFields.filter(field => {
      const value = profileData[field as keyof typeof profileData];
      if (Array.isArray(value)) {
        return value.length > 0;
      }
      return value !== undefined && value !== '';
    });

    return Math.round((filledFields.length / requiredFields.length) * 100);
  };

  // Vérifier l'authentification et charger le profil au chargement
  useEffect(() => {
    const checkUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUserId(user.id);
        await loadUserProfile(user.id);
      }
    };
    checkUser();
  }, []);

  // Charger le profil utilisateur depuis Supabase
  const loadUserProfile = async (userId: string) => {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error) throw error;

      if (data) {
        setProfileData(data);
        setProfileCompleted(true);
        // Charger l'historique des interactions
        if (data.interaction_history) {
          const history = data.interaction_history.map((interaction: { question: string; response: string }) => ({
            type: 'assistant' as const,
            content: `Q: ${interaction.question}\nR: ${interaction.response}`
          }));
          setChatHistory(history);
        }
      }
    } catch (error) {
      console.error('Erreur lors du chargement du profil:', error);
    }
  };

  // Sauvegarder le profil utilisateur dans Supabase
  const saveUserProfile = async (profileData: Partial<UserProfile>) => {
    if (!userId) return;

    try {
      const { error } = await supabase
        .from('user_profiles')
        .upsert({
          user_id: userId,
          ...profileData,
          updated_at: new Date().toISOString()
        });

      if (error) throw error;
    } catch (error) {
      console.error('Erreur lors de la sauvegarde du profil:', error);
    }
  };

  // Mettre à jour le profil avec les insights de la conversation
  const updateProfileWithInsights = async (question: string, response: string, insights: Array<{ type: string, value: string, confidence: number }>) => {
    if (!userId) return;

    try {
      const { data: currentProfile } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (!currentProfile) return;

      // Mettre à jour le profil avec les nouveaux insights
      const updatedProfile = { ...currentProfile };
      insights.forEach(insight => {
        if (insight.confidence > 0.8) { // Seuil de confiance pour la mise à jour
          updatedProfile[insight.type as keyof UserProfile] = insight.value;
        }
      });

      // Ajouter l'interaction à l'historique
      const newInteraction = {
        question,
        response,
        timestamp: new Date().toISOString(),
        insights
      };

      updatedProfile.interaction_history = [
        ...(currentProfile.interaction_history || []),
        newInteraction
      ].slice(-50); // Garder les 50 dernières interactions

      // Sauvegarder les modifications
      const { error } = await supabase
        .from('user_profiles')
        .update(updatedProfile)
        .eq('user_id', userId);

      if (error) throw error;

      // Mettre à jour l'état local
      setProfileData(updatedProfile);
    } catch (error) {
      console.error('Erreur lors de la mise à jour du profil:', error);
    }
  };

  const handleProfileSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!userId) return;

    setIsLoading(true);
    try {
      await saveUserProfile(profileData);
      setProfileCompleted(true);

      const { recommendations, opportunities } = analyzeProfile();
      const welcomeMessage = `Bonjour ! Je suis Francis, votre conseiller fiscal intelligent. \n\nBasé sur votre profil, je peux vous aider à optimiser votre fiscalité. Voici mes premières recommandations :\n\n${recommendations.map(rec => `• ${rec}`).join('\n')}\n\nOpportunités identifiées :\n${opportunities.map(opp => `• ${opp}`).join('\n')}\n\nPosez-moi des questions spécifiques pour en savoir plus sur ces recommandations.`;

      setChatHistory([{ type: 'assistant', content: welcomeMessage }]);
      setActiveTab('chat');
    } catch (error) {
      console.error('Erreur lors de la sauvegarde du profil:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!question.trim() || !userId) return;

    setIsLoading(true);
    const userQuestion = question;
    setQuestion('');

    setChatHistory(prev => [...prev, { type: 'user', content: userQuestion }]);

    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: userQuestion,
          profile: profileData,
          chatMode: chatMode,
          userId: userId
        }),
      });

      if (!response.ok) {
        throw new Error('Erreur de connexion avec Francis');
      }

      const data = await response.json();
      setChatHistory(prev => [...prev, { type: 'assistant', content: data.response }]);

      // Mettre à jour le profil avec les insights de la réponse
      if (data.insights) {
        await updateProfileWithInsights(userQuestion, data.response, data.insights);
      }
    } catch (error) {
      console.error('Erreur:', error);
      setChatHistory(prev => [...prev, { 
        type: 'assistant', 
        content: "Désolé, je rencontre des difficultés techniques. Veuillez réessayer dans quelques instants." 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Nouvelle fonction pour gérer le téléchargement de fichier pour l'OCR
  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      // TODO: Implémenter l'endpoint backend /upload-tax-form
      const response = await fetch('http://localhost:8000/upload-tax-form', {
        method: 'POST',
        body: formData,
        // Le header 'Content-Type' est automatiquement défini par FormData
      });

      if (!response.ok) {
        throw new Error('Erreur lors du téléchargement du fichier');
      }

      const result = await response.json();
      console.log('OCR Result:', result);
      // TODO: Afficher le résultat de l'OCR dans l'interface utilisateur
      setChatHistory(prev => [...prev, { type: 'assistant', content: `Résultat OCR pour ${file.name}:\n\n` + JSON.stringify(result, null, 2) }]);
      setActiveTab('chat');

    } catch (error) {
      console.error('Erreur OCR:', error);
      setChatHistory(prev => [...prev, { 
        type: 'assistant', 
        content: "Désolé, une erreur est survenue lors du traitement OCR de votre fichier." 
      }]);
      setActiveTab('chat');
    } finally {
      setIsLoading(false);
    }
  };

  const analyzeProfile = () => {
    const { situation, revenus, patrimoine, objectifs, toleranceRisque, horizonInvestissement } = profileData;

    let recommendations = [];
    let opportunities = [];

    // Analyse de la situation familiale
    if (situation === 'marie' || situation === 'pacs') {
      recommendations.push("Optimisation du quotient familial");
      opportunities.push("Déclaration commune pour optimiser les tranches d'imposition");
    }

    // Analyse des revenus
    if (revenus === '50k-100k') {
      recommendations.push("Optimisation de la TMI avec le PER");
      opportunities.push("Défiscalisation via l'investissement locatif");
    } else if (revenus === '100k-200k') {
      recommendations.push("Mise en place d'une holding");
      opportunities.push("Optimisation IS/IR");
    } else if (revenus === '>200k') {
      recommendations.push("Stratégie de défiscalisation globale");
      opportunities.push("Optimisation des niches fiscales");
    }

    // Analyse du patrimoine
    if (patrimoine === '100k-300k') {
      recommendations.push("Diversification patrimoniale");
      opportunities.push("Investissement en SCPI");
    } else if (patrimoine === '300k-500k') {
      recommendations.push("Stratégie de transmission anticipée");
      opportunities.push("Optimisation des droits de succession");
    } else if (patrimoine === '500k-1M' || patrimoine === '>1M') {
      recommendations.push("Stratégie patrimoniale globale");
      opportunities.push("Optimisation fiscale multi-supports");
    }

    // Analyse des objectifs
    if (objectifs?.includes('retraite')) {
      recommendations.push("PER et assurance vie");
      opportunities.push("Défiscalisation retraite");
    } else if (objectifs?.includes('patrimoine')) {
      recommendations.push("Donation progressive");
      opportunities.push("Optimisation des abattements");
    } else if (objectifs?.includes('investissement')) {
      recommendations.push("Mix d'investissements défiscalisés");
      opportunities.push("Optimisation des plus-values");
    } else if (objectifs?.includes('protection')) {
      recommendations.push("Assurance décès et invalidité");
      opportunities.push("Protection du conjoint et des enfants");
    }

    // Analyse du profil de risque
    if (toleranceRisque === 'conservateur') {
      recommendations.push("Investissements défiscalisés sécurisés");
      opportunities.push("SCPI de rendement");
    } else if (toleranceRisque === 'modere') {
      recommendations.push("Mix d'investissements équilibré");
      opportunities.push("Diversification multi-supports");
    } else if (toleranceRisque === 'dynamique') {
      recommendations.push("Investissements à fort potentiel");
      opportunities.push("Optimisation des plus-values");
    }

    // Analyse de l'horizon d'investissement
    if (horizonInvestissement === 'court') {
      recommendations.push("Investissements liquides");
      opportunities.push("Optimisation court terme");
    } else if (horizonInvestissement === 'moyen') {
      recommendations.push("Investissements mixtes");
      opportunities.push("Diversification temporelle");
    } else if (horizonInvestissement === 'long') {
      recommendations.push("Investissements de long terme");
      opportunities.push("Optimisation transmission");
    }

    return { recommendations, opportunities };
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#1a2942] via-[#223c63] to-[#234876]">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-[#1a2942]/95 backdrop-blur-lg border-r border-[#c5a572]/20 transform transition-transform duration-300 ease-in-out ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}`}>
        <div className="flex flex-col h-full">
          {/* Logo et bouton de fermeture */}
          <div className="flex items-center justify-between p-4 border-b border-[#c5a572]/20">
            <h2 className="text-xl font-bold text-white">Scrapsnpip</h2>
            <button
              onClick={() => setIsSidebarOpen(false)}
              className="p-2 text-gray-400 hover:text-white focus:outline-none"
            >
              <X className="w-6 h-6" />
            </button>
          </div>

          {/* Menu items */}
          <nav className="flex-1 p-4 space-y-2">
            <button
              onClick={() => setActiveTab('home')}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                activeTab === 'home' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <UserCircle className="w-5 h-5" />
              <span>Profil</span>
            </button>

            <button
              onClick={() => setActiveTab('chat')}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                activeTab === 'chat' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <MessageSquare className="w-5 h-5" />
              <span>Chat</span>
            </button>

            <button
              onClick={() => setActiveTab('analytics')}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                activeTab === 'analytics' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <TrendingUp className="w-5 h-5" />
              <span>Analytics</span>
            </button>

            <button
              onClick={() => setActiveTab('documents')}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                activeTab === 'documents' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <FileText className="w-5 h-5" />
              <span>Documents</span>
            </button>

            <button
              onClick={() => setActiveTab('settings')}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                activeTab === 'settings' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <Settings className="w-5 h-5" />
              <span>Paramètres</span>
            </button>
          </nav>

          {/* User profile section */}
          <div className="p-4 border-t border-[#c5a572]/20">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 rounded-full bg-[#c5a572] flex items-center justify-center">
                <User className="w-6 h-6 text-[#1a2942]" />
              </div>
              <div>
                <p className="text-sm font-medium text-white">Utilisateur</p>
                <p className="text-xs text-gray-400">Voir le profil</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Bouton pour ouvrir le menu */}
      {!isSidebarOpen && (
        <button
          onClick={() => setIsSidebarOpen(true)}
          className="fixed top-4 left-4 z-50 p-2 text-white bg-[#1a2942]/80 rounded-lg hover:bg-[#1a2942] focus:outline-none"
        >
          <Menu className="w-6 h-6" />
        </button>
      )}

      {/* Contenu principal */}
      <div className={`transition-all duration-300 ${isSidebarOpen ? 'ml-64' : 'ml-0'}`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="bg-[#1a2942]/95 backdrop-blur-lg rounded-2xl shadow-xl overflow-hidden border border-[#c5a572]/20">
            {/* En-tête avec barre de progression */}
            <div className="bg-gradient-to-r from-[#1a2942] to-[#223c63] px-6 py-8 border-b border-[#c5a572]/20">
              <h1 className="text-3xl font-bold text-white mb-4">
                Votre Profil Fiscal
              </h1>
              <div className="space-y-2">
                <div className="flex justify-between text-sm text-gray-300">
                  <span>Progression</span>
                  <span>{calculateProgress()}%</span>
                </div>
                <div className="h-2 bg-white/10 rounded-full overflow-hidden">
                  <div 
                    className="h-full bg-[#c5a572] transition-all duration-500 ease-out"
                    style={{ width: `${calculateProgress()}%` }}
                  />
                </div>
              </div>
            </div>

            {/* Contenu du formulaire */}
            <div className="p-6">
              <form onSubmit={handleSubmit} className="space-y-8">
                {/* Section Situation Personnelle */}
                <div className="bg-white/5 rounded-xl border border-[#c5a572]/20 overflow-hidden">
                  <div className="bg-gradient-to-r from-[#1a2942] to-[#223c63] px-6 py-4 border-b border-[#c5a572]/20">
                    <h2 className="text-xl font-semibold text-white flex items-center">
                      <UserCircle className="w-6 h-6 mr-2 text-[#c5a572]" />
                      Situation Personnelle
                    </h2>
                  </div>
                  <div className="p-6 space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Situation Familiale
                          <span className="text-[#c5a572] ml-1">*</span>
                        </label>
                        <select
                          value={profileData.situationFamiliale}
                          onChange={(e) => setProfileData({...profileData, situationFamiliale: e.target.value})}
                          className="w-full rounded-lg bg-white/5 border border-gray-600 text-white focus:border-[#c5a572] focus:ring-[#c5a572]"
                          required
                        >
                          <option value="">Sélectionnez votre situation</option>
                          <option value="celibataire">Célibataire</option>
                          <option value="marie">Marié(e)</option>
                          <option value="pacs">PACS</option>
                          <option value="divorce">Divorcé(e)</option>
                          <option value="veuf">Veuf(ve)</option>
                        </select>
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Nombre d'Enfants
                          <span className="text-[#c5a572] ml-1">*</span>
                        </label>
                        <input
                          type="number"
                          value={profileData.nombreEnfants}
                          onChange={(e) => setProfileData({...profileData, nombreEnfants: parseInt(e.target.value)})}
                          className="w-full rounded-lg bg-white/5 border border-gray-600 text-white focus:border-[#c5a572] focus:ring-[#c5a572]"
                          min="0"
                          required
                        />
                      </div>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">
                        Localisation
                        <span className="text-[#c5a572] ml-1">*</span>
                      </label>
                      <input
                        type="text"
                        value={profileData.localisation}
                        onChange={(e) => setProfileData({...profileData, localisation: e.target.value})}
                        className="w-full rounded-lg bg-white/5 border border-gray-600 text-white focus:border-[#c5a572] focus:ring-[#c5a572]"
                        placeholder="Ville, département"
                        required
                      />
                    </div>
                  </div>
                </div>

                {/* Section Situation Professionnelle */}
                <div className="bg-white/5 rounded-xl border border-[#c5a572]/20 overflow-hidden">
                  <div className="bg-gradient-to-r from-[#1a2942] to-[#223c63] px-6 py-4 border-b border-[#c5a572]/20">
                    <h2 className="text-xl font-semibold text-white flex items-center">
                      <Briefcase className="w-6 h-6 mr-2 text-[#c5a572]" />
                      Situation Professionnelle
                    </h2>
                  </div>
                  <div className="p-6 space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Statut Professionnel
                          <span className="text-[#c5a572] ml-1">*</span>
                        </label>
                        <select
                          value={profileData.situationProfessionnelle}
                          onChange={(e) => setProfileData({...profileData, situationProfessionnelle: e.target.value})}
                          className="w-full rounded-lg bg-white/5 border border-gray-600 text-white focus:border-[#c5a572] focus:ring-[#c5a572]"
                          required
                        >
                          <option value="">Sélectionnez votre statut</option>
                          <option value="salarie">Salarié</option>
                          <option value="independant">Indépendant</option>
                          <option value="retraite">Retraité</option>
                          <option value="chomeur">Chômeur</option>
                        </select>
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Secteur d'Activité
                          <span className="text-[#c5a572] ml-1">*</span>
                        </label>
                        <input
                          type="text"
                          value={profileData.secteurActivite}
                          onChange={(e) => setProfileData({...profileData, secteurActivite: e.target.value})}
                          className="w-full rounded-lg bg-white/5 border border-gray-600 text-white focus:border-[#c5a572] focus:ring-[#c5a572]"
                          placeholder="Ex: Informatique, Santé, etc."
                          required
                        />
                      </div>
                    </div>
                  </div>
                </div>

                {/* Section Situation Fiscale */}
                <div className="bg-white/5 rounded-xl border border-[#c5a572]/20 overflow-hidden">
                  <div className="bg-gradient-to-r from-[#1a2942] to-[#223c63] px-6 py-4 border-b border-[#c5a572]/20">
                    <h2 className="text-xl font-semibold text-white flex items-center">
                      <Scale className="w-6 h-6 mr-2 text-[#c5a572]" />
                      Situation Fiscale
                    </h2>
                  </div>
                  <div className="p-6 space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Régime d'Imposition
                          <span className="text-[#c5a572] ml-1">*</span>
                        </label>
                        <select
                          value={profileData.regimeImposition}
                          onChange={(e) => setProfileData({...profileData, regimeImposition: e.target.value})}
                          className="w-full rounded-lg bg-white/5 border border-gray-600 text-white focus:border-[#c5a572] focus:ring-[#c5a572]"
                          required
                        >
                          <option value="">Sélectionnez votre régime</option>
                          <option value="micro">Micro-entreprise</option>
                          <option value="reel">Régime réel</option>
                          <option value="auto">Auto-entrepreneur</option>
                        </select>
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Zone Fiscale
                          <span className="text-[#c5a572] ml-1">*</span>
                        </label>
                        <select
                          value={profileData.zoneFiscale}
                          onChange={(e) => setProfileData({...profileData, zoneFiscale: e.target.value})}
                          className="w-full rounded-lg bg-white/5 border border-gray-600 text-white focus:border-[#c5a572] focus:ring-[#c5a572]"
                          required
                        >
                          <option value="">Sélectionnez votre zone</option>
                          <option value="zone1">Zone 1</option>
                          <option value="zone2">Zone 2</option>
                          <option value="zone3">Zone 3</option>
                        </select>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Section Patrimoine et Investissements */}
                <div className="bg-white/5 rounded-xl border border-[#c5a572]/20 overflow-hidden">
                  <div className="bg-gradient-to-r from-[#1a2942] to-[#223c63] px-6 py-4 border-b border-[#c5a572]/20">
                    <h2 className="text-xl font-semibold text-white flex items-center">
                      <PiggyBank className="w-6 h-6 mr-2 text-[#c5a572]" />
                      Patrimoine et Investissements
                    </h2>
                  </div>
                  <div className="p-6 space-y-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">
                        Composition du Patrimoine
                        <span className="text-[#c5a572] ml-1">*</span>
                      </label>
                      <div className="space-y-2">
                        {['Immobilier', 'Actions', 'Obligations', 'Assurance-vie', 'SCPI'].map((type) => (
                          <label key={type} className="flex items-center space-x-3">
                            <input
                              type="checkbox"
                              checked={profileData.compositionPatrimoine?.includes(type) || false}
                              onChange={(e) => {
                                const newTypes = e.target.checked
                                  ? [...(profileData.compositionPatrimoine || []), type]
                                  : (profileData.compositionPatrimoine || []).filter(t => t !== type);
                                setProfileData({...profileData, compositionPatrimoine: newTypes});
                              }}
                              className="h-4 w-4 text-[#c5a572] focus:ring-[#c5a572] border-gray-600 rounded bg-white/5"
                            />
                            <span className="text-gray-300">{type}</span>
                          </label>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Section Objectifs */}
                <div className="bg-white/5 rounded-xl border border-[#c5a572]/20 overflow-hidden">
                  <div className="bg-gradient-to-r from-[#1a2942] to-[#223c63] px-6 py-4 border-b border-[#c5a572]/20">
                    <h2 className="text-xl font-semibold text-white flex items-center">
                      <Target className="w-6 h-6 mr-2 text-[#c5a572]" />
                      Objectifs
                    </h2>
                  </div>
                  <div className="p-6 space-y-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">
                        Objectifs Financiers
                        <span className="text-[#c5a572] ml-1">*</span>
                      </label>
                      <div className="space-y-2">
                        {['Retraite', 'Achat immobilier', 'Transmission', 'Indépendance financière'].map((objectif) => (
                          <label key={objectif} className="flex items-center space-x-3">
                            <input
                              type="checkbox"
                              checked={profileData.objectifsFinanciers?.includes(objectif) || false}
                              onChange={(e) => {
                                const newObjectifs = e.target.checked
                                  ? [...(profileData.objectifsFinanciers || []), objectif]
                                  : (profileData.objectifsFinanciers || []).filter(o => o !== objectif);
                                setProfileData({...profileData, objectifsFinanciers: newObjectifs});
                              }}
                              className="h-4 w-4 text-[#c5a572] focus:ring-[#c5a572] border-gray-600 rounded bg-white/5"
                            />
                            <span className="text-gray-300">{objectif}</span>
                          </label>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Bouton de soumission */}
                <div className="flex justify-end">
                  <button
                    type="submit"
                    className="px-6 py-3 bg-[#c5a572] text-[#1a2942] font-medium rounded-lg shadow-lg hover:bg-[#d4b583] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#c5a572] transition-all duration-200"
                  >
                    Enregistrer mon profil
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
