import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageSquare, 
  TrendingUp, 
  Shield, 
  Settings, 
  Menu, 
  X, 
  Send, 
  FileText, 
  PieChart,
  ChevronRight,
  Bell,
  Search,
  Plus,
  User
} from 'lucide-react';
import { Link } from 'react-router-dom';
import { supabase, UserProfile } from '../lib/supabase';

export function Dashboard() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [question, setQuestion] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState<Array<{ type: 'user' | 'assistant', content: string }>>([]);
  const [activeTab, setActiveTab] = useState('home');
  const [profileData, setProfileData] = useState<Partial<UserProfile>>({
    situation: '',
    revenus: '',
    patrimoine: '',
    objectifs: [],
    toleranceRisque: '',
    horizonInvestissement: '',
    nombreEnfants: 0,
    agesEnfants: '',
    typeRevenus: [],
    autresRevenus: '',
    situationProfessionnelle: '',
    statutFiscal: '',
    regimeImposition: '',
    investissementsExistants: [],
    projetsImmobiliers: '',
    besoinsRetraite: '',
    situationFamiliale: '',
    localisation: '',
    zoneFiscale: '',
    secteurActivite: '',
    revenusPassifs: '',
    dettes: '',
    objectifsFinanciers: [],
    contraintesFiscales: [],
    compositionPatrimoine: []
  });

  const [profileCompleted, setProfileCompleted] = useState(false);
  const [chatMode, setChatMode] = useState('libre');
  const [userId, setUserId] = useState<string | null>(null);

  // Vérifier l'authentification et charger le profil au chargement
  useEffect(() => {
    const checkUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUserId(user.id);
        await loadUserProfile(user.id);
      }
    };
    checkUser();
  }, []);

  // Charger le profil utilisateur depuis Supabase
  const loadUserProfile = async (userId: string) => {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error) throw error;

      if (data) {
        setProfileData(data);
        setProfileCompleted(true);
        // Charger l'historique des interactions
        if (data.interaction_history) {
          const history = data.interaction_history.map((interaction: { question: string; response: string }) => ({
            type: 'assistant' as const,
            content: `Q: ${interaction.question}\nR: ${interaction.response}`
          }));
          setChatHistory(history);
        }
      }
    } catch (error) {
      console.error('Erreur lors du chargement du profil:', error);
    }
  };

  // Sauvegarder le profil utilisateur dans Supabase
  const saveUserProfile = async (profileData: Partial<UserProfile>) => {
    if (!userId) return;

    try {
      const { error } = await supabase
        .from('user_profiles')
        .upsert({
          user_id: userId,
          ...profileData,
          updated_at: new Date().toISOString()
        });

      if (error) throw error;
    } catch (error) {
      console.error('Erreur lors de la sauvegarde du profil:', error);
    }
  };

  // Mettre à jour le profil avec les insights de la conversation
  const updateProfileWithInsights = async (question: string, response: string, insights: Array<{ type: string, value: string, confidence: number }>) => {
    if (!userId) return;

    try {
      const { data: currentProfile } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (!currentProfile) return;

      // Mettre à jour le profil avec les nouveaux insights
      const updatedProfile = { ...currentProfile };
      insights.forEach(insight => {
        if (insight.confidence > 0.8) { // Seuil de confiance pour la mise à jour
          updatedProfile[insight.type as keyof UserProfile] = insight.value;
        }
      });

      // Ajouter l'interaction à l'historique
      const newInteraction = {
        question,
        response,
        timestamp: new Date().toISOString(),
        insights
      };

      updatedProfile.interaction_history = [
        ...(currentProfile.interaction_history || []),
        newInteraction
      ].slice(-50); // Garder les 50 dernières interactions

      // Sauvegarder les modifications
      const { error } = await supabase
        .from('user_profiles')
        .update(updatedProfile)
        .eq('user_id', userId);

      if (error) throw error;

      // Mettre à jour l'état local
      setProfileData(updatedProfile);
    } catch (error) {
      console.error('Erreur lors de la mise à jour du profil:', error);
    }
  };

  const handleProfileSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!userId) return;

    setIsLoading(true);
    try {
      await saveUserProfile(profileData);
      setProfileCompleted(true);

      const { recommendations, opportunities } = analyzeProfile();
      const welcomeMessage = `Bonjour ! Je suis Francis, votre conseiller fiscal intelligent. \n\nBasé sur votre profil, je peux vous aider à optimiser votre fiscalité. Voici mes premières recommandations :\n\n${recommendations.map(rec => `• ${rec}`).join('\n')}\n\nOpportunités identifiées :\n${opportunities.map(opp => `• ${opp}`).join('\n')}\n\nPosez-moi des questions spécifiques pour en savoir plus sur ces recommandations.`;

      setChatHistory([{ type: 'assistant', content: welcomeMessage }]);
      setActiveTab('chat');
    } catch (error) {
      console.error('Erreur lors de la sauvegarde du profil:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!question.trim() || !userId) return;

    setIsLoading(true);
    const userQuestion = question;
    setQuestion('');

    setChatHistory(prev => [...prev, { type: 'user', content: userQuestion }]);

    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: userQuestion,
          profile: profileData,
          chatMode: chatMode,
          userId: userId
        }),
      });

      if (!response.ok) {
        throw new Error('Erreur de connexion avec Francis');
      }

      const data = await response.json();
      setChatHistory(prev => [...prev, { type: 'assistant', content: data.response }]);

      // Mettre à jour le profil avec les insights de la réponse
      if (data.insights) {
        await updateProfileWithInsights(userQuestion, data.response, data.insights);
      }
    } catch (error) {
      console.error('Erreur:', error);
      setChatHistory(prev => [...prev, { 
        type: 'assistant', 
        content: "Désolé, je rencontre des difficultés techniques. Veuillez réessayer dans quelques instants." 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Nouvelle fonction pour gérer le téléchargement de fichier pour l'OCR
  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      // TODO: Implémenter l'endpoint backend /upload-tax-form
      const response = await fetch('http://localhost:8000/upload-tax-form', {
        method: 'POST',
        body: formData,
        // Le header 'Content-Type' est automatiquement défini par FormData
      });

      if (!response.ok) {
        throw new Error('Erreur lors du téléchargement du fichier');
      }

      const result = await response.json();
      console.log('OCR Result:', result);
      // TODO: Afficher le résultat de l'OCR dans l'interface utilisateur
      setChatHistory(prev => [...prev, { type: 'assistant', content: `Résultat OCR pour ${file.name}:\n\n` + JSON.stringify(result, null, 2) }]);
      setActiveTab('chat');

    } catch (error) {
      console.error('Erreur OCR:', error);
      setChatHistory(prev => [...prev, { 
        type: 'assistant', 
        content: "Désolé, une erreur est survenue lors du traitement OCR de votre fichier." 
      }]);
      setActiveTab('chat');
    } finally {
      setIsLoading(false);
    }
  };

  const analyzeProfile = () => {
    const { situation, revenus, patrimoine, objectifs, toleranceRisque, horizonInvestissement } = profileData;

    let recommendations = [];
    let opportunities = [];

    // Analyse de la situation familiale
    if (situation === 'marie' || situation === 'pacs') {
      recommendations.push("Optimisation du quotient familial");
      opportunities.push("Déclaration commune pour optimiser les tranches d'imposition");
    }

    // Analyse des revenus
    if (revenus === '50k-100k') {
      recommendations.push("Optimisation de la TMI avec le PER");
      opportunities.push("Défiscalisation via l'investissement locatif");
    } else if (revenus === '100k-200k') {
      recommendations.push("Mise en place d'une holding");
      opportunities.push("Optimisation IS/IR");
    } else if (revenus === '>200k') {
      recommendations.push("Stratégie de défiscalisation globale");
      opportunities.push("Optimisation des niches fiscales");
    }

    // Analyse du patrimoine
    if (patrimoine === '100k-300k') {
      recommendations.push("Diversification patrimoniale");
      opportunities.push("Investissement en SCPI");
    } else if (patrimoine === '300k-500k') {
      recommendations.push("Stratégie de transmission anticipée");
      opportunities.push("Optimisation des droits de succession");
    } else if (patrimoine === '500k-1M' || patrimoine === '>1M') {
      recommendations.push("Stratégie patrimoniale globale");
      opportunities.push("Optimisation fiscale multi-supports");
    }

    // Analyse des objectifs
    if (objectifs?.includes('retraite')) {
      recommendations.push("PER et assurance vie");
      opportunities.push("Défiscalisation retraite");
    } else if (objectifs?.includes('patrimoine')) {
      recommendations.push("Donation progressive");
      opportunities.push("Optimisation des abattements");
    } else if (objectifs?.includes('investissement')) {
      recommendations.push("Mix d'investissements défiscalisés");
      opportunities.push("Optimisation des plus-values");
    } else if (objectifs?.includes('protection')) {
      recommendations.push("Assurance décès et invalidité");
      opportunities.push("Protection du conjoint et des enfants");
    }

    // Analyse du profil de risque
    if (toleranceRisque === 'conservateur') {
      recommendations.push("Investissements défiscalisés sécurisés");
      opportunities.push("SCPI de rendement");
    } else if (toleranceRisque === 'modere') {
      recommendations.push("Mix d'investissements équilibré");
      opportunities.push("Diversification multi-supports");
    } else if (toleranceRisque === 'dynamique') {
      recommendations.push("Investissements à fort potentiel");
      opportunities.push("Optimisation des plus-values");
    }

    // Analyse de l'horizon d'investissement
    if (horizonInvestissement === 'court') {
      recommendations.push("Investissements liquides");
      opportunities.push("Optimisation court terme");
    } else if (horizonInvestissement === 'moyen') {
      recommendations.push("Investissements mixtes");
      opportunities.push("Diversification temporelle");
    } else if (horizonInvestissement === 'long') {
      recommendations.push("Investissements de long terme");
      opportunities.push("Optimisation transmission");
    }

    return { recommendations, opportunities };
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#1a2942] via-[#223c63] to-[#234876] overflow-hidden">
      {/* Mobile Menu Button */}
      <button 
        onClick={() => setIsSidebarOpen(true)}
        className="lg:hidden fixed top-4 left-4 z-50 p-2 rounded-lg bg-[#1a2942]/95 backdrop-blur-lg text-white"
      >
        <Menu className="h-6 w-6" />
      </button>

      {/* Sidebar */}
      <motion.aside
        initial={{ x: -300 }}
        animate={{ x: isSidebarOpen ? 0 : -300 }}
        className="fixed top-0 left-0 h-full w-72 bg-[#1a2942]/95 backdrop-blur-lg border-r border-[#c5a572]/20 z-50 lg:translate-x-0"
      >
        {/* Logo dans la sidebar pour mobile */}
        <div className="p-4 lg:p-6 flex items-center gap-3 lg:hidden">
          <img src="/fiscalia-logo.svg" alt="Fiscal.ia Logo" className="h-8 w-8" />
          <div>
            <h1 className="text-xl font-bold text-white font-serif">Fiscal.IA</h1>
            <p className="text-xs text-gray-400">Votre assistant fiscal intelligent</p>
          </div>
        </div>
        <div className="p-4 lg:p-6 pt-4 lg:pt-6"> {/* Ajustement padding supérieur pour laisser espace au logo sur mobile */}
          <nav className="space-y-2">
            <button
              onClick={() => setActiveTab('home')}
              className={`w-full flex items-center gap-3 px-3 lg:px-4 py-2 lg:py-3 rounded-xl transition-all ${
                activeTab === 'home' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <Shield className="h-5 w-5" />
              <span className="text-sm lg:text-base">Accueil</span>
            </button>
            <button
              onClick={() => setActiveTab('profile')}
              className={`w-full flex items-center gap-3 px-3 lg:px-4 py-2 lg:py-3 rounded-xl transition-all ${
                activeTab === 'profile' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <User className="h-5 w-5" />
              <span className="text-sm lg:text-base">Profil</span>
            </button>
            <button
              onClick={() => setActiveTab('chat')}
              className={`w-full flex items-center gap-3 px-3 lg:px-4 py-2 lg:py-3 rounded-xl transition-all ${
                activeTab === 'chat' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <MessageSquare className="h-5 w-5" />
              <span className="text-sm lg:text-base">Chat</span>
            </button>
             {/* Nouveau bouton OCR */}
            <button
              onClick={() => setActiveTab('ocr')}
              className={`w-full flex items-center gap-3 px-3 lg:px-4 py-2 lg:py-3 rounded-xl transition-all ${
                activeTab === 'ocr' 
                  ? 'bg-[#c5a572] text-[#1a2942]' 
                  : 'text-gray-300 hover:bg-white/5'
              }`}
            >
              <FileText className="h-5 w-5" />
              <span className="text-sm lg:text-base">Scanner Fiche Impôts</span>
            </button>
          </nav>
        </div>
      </motion.aside>

      {/* Main Content */}
      <main className={`transition-all duration-300 ${isSidebarOpen ? 'lg:ml-72' : ''} pt-4 lg:pt-8`}> {/* Ajustement du padding supérieur */}
        <div className="p-4 lg:p-8">
          {activeTab === 'home' && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="max-w-6xl mx-auto"
            >
              {/* Hero Section Améliorée pour la Découverte */}
              <div className="text-center mb-12 relative">
                <motion.div
                  initial={{ scale: 0.8, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  transition={{ duration: 0.5 }}
                  className="relative z-10"
                >
                  <h1 className="text-4xl lg:text-6xl font-bold text-white mb-4 bg-clip-text text-transparent bg-gradient-to-r from-[#c5a572] to-[#e6c99f]">
                    Bienvenue sur Francis
                  </h1>
                  <p className="text-gray-300 text-lg lg:text-xl max-w-2xl mx-auto mb-6">
                    Votre assistant fiscal intelligent qui vous aide à optimiser votre situation et à mieux comprendre les lois.<br/>**Commencez par remplir votre profil pour des conseils sur mesure !**
                  </p>
                  {/* Bouton d'appel à l'action principal */}
                  <motion.button
                     whileHover={{ scale: 1.05 }}
                     whileTap={{ scale: 0.95 }}
                     onClick={() => setActiveTab('profile')}
                     className="bg-[#c5a572] text-[#1a2942] font-bold py-3 px-8 rounded-full text-lg shadow-lg hover:bg-[#e6c99f] transition-colors"
                  >
                     Configurer mon Profil
                  </motion.button>
                </motion.div>

                {/* Animated Background */}
                <div className="absolute inset-0 -z-10">
                  <div className="absolute inset-0 bg-gradient-to-r from-[#1a2942]/50 via-[#223c63]/50 to-[#234876]/50 backdrop-blur-3xl" />
                  <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,#ffffff10_1px,transparent_1px)] bg-[size:20px_20px] opacity-20" />
                  <div className="absolute inset-0 bg-gradient-to-b from-transparent via-[#c5a572]/5 to-transparent" />
                </div>
              </div>

              {/* Main Features Grid - peut-être ajuster l'ordre */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-12">
                {/* Carte Profil - laisser en premier pour le parcours suggéré */}
                <motion.div
                  whileHover={{ scale: 1.02, y: -5 }}
                  onClick={() => setActiveTab('profile')}
                  className="group bg-white/5 backdrop-blur-sm rounded-2xl p-6 border border-[#c5a572]/20 cursor-pointer hover:border-[#c5a572]/40 transition-all"
                >
                  <div className="flex items-center gap-4 mb-4">
                    <div className="w-12 h-12 rounded-xl bg-[#c5a572]/20 flex items-center justify-center group-hover:bg-[#c5a572]/30 transition-colors">
                      <User className="h-6 w-6 text-[#c5a572]" />
                    </div>
                    <h3 className="text-xl font-bold text-white">Profil Fiscal</h3>
                  </div>
                  <p className="text-gray-300 mb-4">Indispensable pour recevoir des conseils personnalisés. Renseignez votre situation.</p>
                  <div className="flex items-center text-[#c5a572] text-sm">
                    <span>Commencer</span>
                    <ChevronRight className="h-4 w-4 ml-1" />
                  </div>
                </motion.div>

                {/* Carte Chat */}
                <motion.div
                  whileHover={{ scale: 1.02, y: -5 }}
                  onClick={() => setActiveTab('chat')}
                  className="group bg-white/5 backdrop-blur-sm rounded-2xl p-6 border border-[#c5a572]/20 cursor-pointer hover:border-[#c5a572]/40 transition-all"
                >
                  <div className="flex items-center gap-4 mb-4">
                    <div className="w-12 h-12 rounded-xl bg-[#c5a572]/20 flex items-center justify-center group-hover:bg-[#c5a572]/30 transition-colors">
                      <MessageSquare className="h-6 w-6 text-[#c5a572]" />
                    </div>
                    <h3 className="text-xl font-bold text-white">Chat avec Francis</h3>
                  </div>
                  <p className="text-gray-300 mb-4">Posez toutes vos questions fiscales et obtenez des réponses claires.</p>
                  <div className="flex items-center text-[#c5a572] text-sm">
                    <span>Discuter maintenant</span>
                    <ChevronRight className="h-4 w-4 ml-1" />
                  </div>
                </motion.div>

                {/* Carte Scanner Fiche Impôts - Mettre en avant pour l'OCR */}
                 <motion.div
                  whileHover={{ scale: 1.02, y: -5 }}
                  onClick={() => setActiveTab('ocr')}
                  className="group bg-white/5 backdrop-blur-sm rounded-2xl p-6 border border-[#c5a572]/20 cursor-pointer hover:border-[#c5a572]/40 transition-all"
                >
                  <div className="flex items-center gap-4 mb-4">
                    <div className="w-12 h-12 rounded-xl bg-[#c5a572]/20 flex items-center justify-center group-hover:bg-[#c5a572]/30 transition-colors">
