                        <option value="aucun">Aucun besoin</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">Horizon d'investissement</label>
                      <select
                        value={profileData.horizonInvestissement}
                        onChange={(e) => setProfileData({...profileData, horizonInvestissement: e.target.value})}
                        className="w-full px-4 py-2 rounded-lg bg-white/5 border border-gray-600 text-white focus:outline-none focus:border-[#c5a572]"
                      >
                        <option value="">Sélectionnez...</option>
                        <option value="court">Court terme (&lt; 3 ans)</option>
                        <option value="moyen">Moyen terme (3-7 ans)</option>
                        <option value="long">Long terme (&gt; 7 ans)</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">Tolérance au risque</label>
                      <select
                        value={profileData.toleranceRisque}
                        onChange={(e) => setProfileData({...profileData, toleranceRisque: e.target.value})}
                        className="w-full px-4 py-2 rounded-lg bg-white/5 border border-gray-600 text-white focus:outline-none focus:border-[#c5a572]"
                      >
                        <option value="">Sélectionnez...</option>
                        <option value="conservateur">Conservateur</option>
                        <option value="modere">Modéré</option>
                        <option value="dynamique">Dynamique</option>
                        <option value="agressif">Agressif</option>
                      </select>
                    </div>
                  </div>
                </div>

                {/* Section Objectifs et Contraintes */}
                <div className="bg-white/5 rounded-2xl p-6 space-y-6">
                  <h3 className="text-xl font-bold text-white mb-4">Objectifs et Contraintes</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">Objectifs principaux</label>
                      <select
                        value={profileData.objectifs}
                        onChange={(e) => setProfileData({...profileData, objectifs: Array.from(e.target.selectedOptions, option => option.value)})}
                        className="w-full px-4 py-2 rounded-lg bg-white/5 border border-gray-600 text-white focus:outline-none focus:border-[#c5a572]"
                        multiple
                      >
                        <option value="retraite">Préparation de la retraite</option>
                        <option value="patrimoine">Transmission du patrimoine</option>
                        <option value="investissement">Investissement</option>
                        <option value="protection">Protection de la famille</option>
                        <option value="optimisation">Optimisation fiscale</option>
                        <option value="transmission">Transmission anticipée</option>
                        <option value="defiscalisation">Défiscalisation</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">Contraintes fiscales</label>
                      <select
                        value={profileData.contraintesFiscales}
                        onChange={(e) => setProfileData({...profileData, contraintesFiscales: Array.from(e.target.selectedOptions, option => option.value)})}
                        className="w-full px-4 py-2 rounded-lg bg-white/5 border border-gray-600 text-white focus:outline-none focus:border-[#c5a572]"
                        multiple
                      >
                        <option value="isf">ISF</option>
                        <option value="droit_succession">Droits de succession</option>
                        <option value="plus_values">Plus-values</option>
                        <option value="revenus_locatifs">Revenus locatifs</option>
                        <option value="dividendes">Dividendes</option>
                        <option value="aucune">Aucune contrainte</option>
                      </select>
                    </div>
                  </div>
                </div>

                <button
                  type="submit"
                  className="w-full lg:w-auto px-8 py-4 bg-[#c5a572] text-[#1a2942] font-bold rounded-lg hover:bg-[#d4b583] transition-colors"
                >
                  Valider mon profil
                </button>
              </form>
            </motion.div>
          )}

          {activeTab === 'chat' && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="max-w-3xl mx-auto"
            >
              {/* Sélecteur de mode de chat */}
              <div className="flex justify-center gap-4 mb-4">
                <button
                  onClick={() => setChatMode('libre')}
                  className={`px-4 py-2 rounded-lg transition-colors ${
                    chatMode === 'libre' 
                      ? 'bg-[#c5a572] text-[#1a2942]'
                      : 'bg-white/10 text-white hover:bg-white/20'
                  }`}
                >
                  Mode Libre
                </button>
                <button
                  onClick={() => setChatMode('guide')}
                  className={`px-4 py-2 rounded-lg transition-colors ${
                    chatMode === 'guide' 
                      ? 'bg-[#c5a572] text-[#1a2942]'
                      : 'bg-white/10 text-white hover:bg-white/20'
                  }`}
                >
                  Mode Guidé
                </button>
              </div>

              <div className="bg-white/5 rounded-2xl p-4 lg:p-6 min-h-[60vh] flex flex-col">
                <div className="flex-1 overflow-y-auto mb-4 space-y-4">
                  {chatHistory.map((message, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, y: 10 }}
                      animate={{ opacity: 1, y: 0 }}
                      className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
                    >
                      <div
                        className={`max-w-[80%] lg:max-w-[70%] p-3 lg:p-4 rounded-2xl ${
                          message.type === 'user'
                            ? 'bg-[#c5a572] text-[#1a2942]'
                            : 'bg-white/10 text-white'
                        }`}
                      >
                        <p className="text-sm lg:text-base whitespace-pre-wrap">{message.content}</p>
                      </div>
                    </motion.div>
                  ))}
                  {isLoading && (
                    <div className="flex justify-start">
                      <div className="bg-white/10 text-white p-4 rounded-2xl">
                        <div className="flex space-x-2">
                          <div className="w-2 h-2 bg-white/50 rounded-full animate-bounce" />
                          <div className="w-2 h-2 bg-white/50 rounded-full animate-bounce delay-100" />
                          <div className="w-2 h-2 bg-white/50 rounded-full animate-bounce delay-200" />
                        </div>
                      </div>
                    </div>
                  )}
                </div>

                <form onSubmit={handleSubmit} className="flex gap-2">
                  <input
                    type="text"
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Posez votre question à Francis..."
                    className="flex-1 px-4 py-2 rounded-lg bg-white/5 border border-gray-600 text-white placeholder-gray-400 focus:outline-none focus:border-[#c5a572]"
                  />
                  <button
                    type="submit"
                    disabled={isLoading}
                    className="px-4 py-2 bg-[#c5a572] text-[#1a2942] rounded-lg hover:bg-[#d4b583] transition-colors disabled:opacity-50"
                  >
                    <Send className="h-5 w-5" />
                  </button>
                </form>
              </div>
            </motion.div>
          )}

          {/* Nouvelle section pour l'OCR */}
          {activeTab === 'ocr' && (
             <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="max-w-2xl mx-auto text-white"
            >
              <h2 className="text-2xl lg:text-3xl font-bold text-white mb-6">Scanner Fiche d'Impôts</h2>
              <div className="bg-white/5 rounded-2xl p-6 space-y-4 flex flex-col items-center justify-center text-center">
                <FileText className="h-16 w-16 text-[#c5a572]" />
                <p className="text-lg text-gray-300">Téléversez votre dernière fiche d'impôts pour une analyse automatique.</p>
                <input 
                  type="file" 
                  accept=".pdf, .png, .jpg, .jpeg" 
                  onChange={handleFileUpload} 
                  className="hidden"
                  id="fileUpload"
                />
                <label 
                  htmlFor="fileUpload" 
                  className="px-6 py-3 bg-[#c5a572] text-[#1a2942] font-medium rounded-lg hover:bg-[#d4b583] transition-colors cursor-pointer"
                >
                  Choisir un fichier
                </label>
                {isLoading && <p className="text-gray-400">Analyse en cours...</p>}
              </div>
               {/* Afficher le résultat OCR ici ou rediriger vers le chat */}
             </motion.div>
          )}

           {/* Nouvelle section pour les Paramètres */}
           {activeTab === 'settings' && (
              <motion.div
               initial={{ opacity: 0, y: 20 }}
               animate={{ opacity: 1, y: 0 }}
               className="max-w-2xl mx-auto text-white"
             >
               <h2 className="text-2xl lg:text-3xl font-bold text-white mb-6">Paramètres</h2>
               <div className="bg-white/5 rounded-2xl p-6 space-y-4">
                 <p>Ici se trouveront les options pour configurer votre compte, les notifications, la connexion à d'autres services, etc.</p>
                 {/* Ajoutez d'autres options de paramètres ici plus tard */}
               </div>
             </motion.div>
           )}

        </div>
      </main>
    </div>
  );
}
