import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageSquare, 
  TrendingUp, 
  Shield, 
  Settings, 
  Menu, 
  X, 
  Send, 
  FileText, 
  PieChart,
  ChevronRight,
  Bell,
  Search,
  Plus,
  User,
  UserCircle,
  Briefcase,
  Scale,
  PiggyBank,
  Target,
  Euro,
  Loader2,
  Upload,
  File,
  CheckCircle2,
  AlertCircle,
  Receipt,
  CreditCard,
  Calculator
} from 'lucide-react';
import { Link } from 'react-router-dom';
import { supabase, UserProfile } from '../lib/supabase';

interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
}

interface TrueLayerSuccessData {
  accessToken: string;
  provider: {
    id: string;
    display_name: string;
  };
}

interface TrueLayerError {
  error: string;
  error_description: string;
}

declare global {
  interface Window {
    TrueLayer: new (config: {
      clientId: string;
      redirectUri: string;
      scope: string[];
      locale: string;
      onSuccess: (data: TrueLayerSuccessData) => void;
      onError: (error: TrueLayerError) => void;
    }) => {
      open: () => void;
    };
  }
}

export function Dashboard() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [question, setQuestion] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState<ChatMessage[]>([
    {
      role: 'assistant',
      content: `Bonjour ! Je suis Francis, votre conseiller fiscal IA ðŸ¤–

Je peux vous aider Ã  :
â€¢ Optimiser vos impÃ´ts
â€¢ Analyser vos documents fiscaux
â€¢ RÃ©pondre Ã  vos questions sur la fiscalitÃ©

Posez-moi votre question ou choisissez un parcours :
1. ðŸŽ¯ Parcours guidÃ©
2. ðŸ’¬ Chat libre

Voici quelques questions que vous pouvez me poser :
â€¢ Comment optimiser ma dÃ©claration d'impÃ´ts ?
â€¢ Quelles sont les meilleures stratÃ©gies de dÃ©fiscalisation ?
â€¢ Comment gÃ©rer ma TVA en tant qu'auto-entrepreneur ?
â€¢ Quels sont les avantages fiscaux pour les investissements immobiliers ?

Je suis lÃ  pour vous accompagner ! ðŸ’¡`
    }
  ]);
  const [activeTab, setActiveTab] = useState('home');
  const [profileData, setProfileData] = useState<Partial<UserProfile>>({
    situation: '',
    revenus: '',
    patrimoine: '',
    objectifs: [],
    toleranceRisque: '',
    horizonInvestissement: '',
    nombreEnfants: 0,
    agesEnfants: '',
    typeRevenus: [],
    autresRevenus: '',
    situationProfessionnelle: '',
    statutFiscal: '',
    regimeImposition: '',
    investissementsExistants: [],
    projetsImmobiliers: '',
    besoinsRetraite: '',
    situationFamiliale: '',
    localisation: '',
    zoneFiscale: '',
    secteurActivite: '',
    revenusPassifs: '',
    dettes: '',
    objectifsFinanciers: [],
    contraintesFiscales: [],
    compositionPatrimoine: [],
    is_active: false,
  });

  const [profileCompleted, setProfileCompleted] = useState(false);
  const [chatMode, setChatMode] = useState('libre');
  const [userId, setUserId] = useState<string | null>(null);
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectedBanks, setConnectedBanks] = useState<string[]>([]);
  const [bankData, setBankData] = useState<any>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState<Array<{
    name: string;
    type: string;
    status: 'processing' | 'success' | 'error';
    data?: any;
  }>>([]);
  const [dragActive, setDragActive] = useState(false);

  // Ajouter des suggestions de questions
  const suggestedQuestions = [
    "Comment optimiser ma dÃ©claration d'impÃ´ts ?",
    "Quelles sont les meilleures stratÃ©gies de dÃ©fiscalisation ?",
    "Comment gÃ©rer ma TVA en tant qu'auto-entrepreneur ?",
    "Quels sont les avantages fiscaux pour les investissements immobiliers ?"
  ];

  const calculateProgress = () => {
    const requiredFields = [
      'situationFamiliale',
      'nombreEnfants',
      'localisation',
      'zoneFiscale',
      'situationProfessionnelle',
      'secteurActivite',
      'regimeImposition',
      'statutFiscal',
      'revenus',
      'typeRevenus',
      'patrimoine',
      'compositionPatrimoine',
      'investissementsExistants',
      'projetsImmobiliers',
      'besoinsRetraite',
      'horizonInvestissement',
      'toleranceRisque',
      'objectifs',
      'contraintesFiscales'
    ];

    const filledFields = requiredFields.filter(field => {
      const value = profileData[field as keyof typeof profileData];
      if (Array.isArray(value)) {
        return value.length > 0;
      }
      return value !== undefined && value !== '';
    });

    return Math.round((filledFields.length / requiredFields.length) * 100);
  };

  // VÃ©rifier l'authentification et charger le profil au chargement
  useEffect(() => {
    const checkUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUserId(user.id);
        await loadUserProfile(user.id);
      }
    };
    checkUser();
  }, []);

  // Charger le profil utilisateur depuis Supabase
  const loadUserProfile = async (userId: string) => {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error) throw error;

      if (data) {
        setProfileData(data);
        setProfileCompleted(true);
        // Charger l'historique des interactions
        if (data.interaction_history) {
          const history = data.interaction_history.map((interaction: { question: string; response: string }) => ({
            role: 'assistant',
            content: `Q: ${interaction.question}\nR: ${interaction.response}`
          }));
          setChatHistory(history);
        }
      }
    } catch (error) {
      console.error('Erreur lors du chargement du profil:', error);
    }
  };

  // Sauvegarder le profil utilisateur dans Supabase
  const saveUserProfile = async (profileData: Partial<UserProfile>) => {
    if (!userId) return;

    try {
      const { error } = await supabase
        .from('user_profiles')
        .upsert({
          user_id: userId,
          ...profileData,
          updated_at: new Date().toISOString()
        });

      if (error) throw error;
    } catch (error) {
      console.error('Erreur lors de la sauvegarde du profil:', error);
    }
  };

  // Mettre Ã  jour le profil avec les insights de la conversation
  const updateProfileWithInsights = async (question: string, response: string, insights: Array<{ type: string, value: string, confidence: number }>) => {
    if (!userId) return;

    try {
      const { data: currentProfile } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (!currentProfile) return;

      // Mettre Ã  jour le profil avec les nouveaux insights
      const updatedProfile = { ...currentProfile };
      insights.forEach(insight => {
        if (insight.confidence > 0.8) { // Seuil de confiance pour la mise Ã  jour
          updatedProfile[insight.type as keyof UserProfile] = insight.value;
        }
      });

      // Ajouter l'interaction Ã  l'historique
      const newInteraction = {
        role: 'assistant',
        content: `Q: ${question}\nR: ${response}`
      };

      updatedProfile.interaction_history = [
        ...(currentProfile.interaction_history || []),
        newInteraction
      ].slice(-50); // Garder les 50 derniÃ¨res interactions

      // Sauvegarder les modifications
      const { error } = await supabase
        .from('user_profiles')
        .update(updatedProfile)
        .eq('user_id', userId);

      if (error) throw error;

      // Mettre Ã  jour l'Ã©tat local
      setProfileData(updatedProfile);
    } catch (error) {
      console.error('Erreur lors de la mise Ã  jour du profil:', error);
    }
  };

  const handleProfileSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!userId) return;

    setIsLoading(true);
    try {
      await saveUserProfile(profileData);
      setProfileCompleted(true);

      const { recommendations, opportunities } = analyzeProfile();
      const welcomeMessage = `Bonjour ! Je suis Francis, votre conseiller fiscal propulsÃ© par intelligence artificielle. \n\nBasÃ© sur votre profil, je peux vous aider Ã  optimiser votre fiscalitÃ©. Voici mes premiÃ¨res recommandations :\n\n${recommendations.map(rec => `â€¢ ${rec}`).join('\n')}\n\nOpportunitÃ©s identifiÃ©es :\n${opportunities.map(opp => `â€¢ ${opp}`).join('\n')}\n\nPosez-moi des questions spÃ©cifiques pour en savoir plus sur ces recommandations.`;

      setChatHistory(prev => [...prev, { role: 'assistant', content: welcomeMessage }]);
      setActiveTab('chat');
    } catch (error) {
      console.error('Erreur lors de la sauvegarde du profil:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Ajouter la fonction pour gÃ©rer le parcours guidÃ©
  const startGuidedPath = (path: string) => {
    let initialMessage = '';
    switch (path) {
      case 'optimisation':
        initialMessage = `Parfait ! CommenÃ§ons l'optimisation de votre fiscalitÃ©. ðŸŽ¯

Pour vous accompagner au mieux, j'ai besoin de quelques informations :
1. Quel est votre statut fiscal actuel ?
2. Quels sont vos principaux revenus ?
3. Avez-vous des investissements en cours ?

Vous pouvez rÃ©pondre Ã  ces questions dans l'ordre qui vous convient.`;
        break;
      case 'documents':
        initialMessage = `TrÃ¨s bien ! Analysons vos documents fiscaux. ðŸ“„

Vous pouvez :
1. Partager vos avis d'imposition
2. TÃ©lÃ©charger vos justificatifs
3. Me poser des questions sur vos documents

Je vous guiderai dans l'analyse et l'optimisation de votre situation.`;
        break;
      case 'conseil':
        initialMessage = `Excellent choix ! Je vais vous donner des conseils personnalisÃ©s. ðŸ’¡

Pour vous conseiller au mieux, dites-moi :
1. Quels sont vos objectifs fiscaux ?
2. Avez-vous des projets spÃ©cifiques ?
3. Quel est votre horizon d'investissement ?

Je vous aiderai Ã  atteindre vos objectifs.`;
        break;
    }

    setChatHistory(prev => [...prev, { role: 'assistant', content: initialMessage }]);
  };

  // Modifier la fonction handleSubmit pour amÃ©liorer la gestion des erreurs
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!question.trim() || !userId) return;

    setIsLoading(true);
    const userQuestion = question;
    setQuestion('');

    // Ajouter la question de l'utilisateur avec une animation
    setChatHistory(prev => [...prev, { role: 'user', content: userQuestion }]);

    try {
      const response = await fetch('http://localhost:8080/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: userQuestion,
          profile: profileData,
          chatMode: chatMode,
          userId: userId,
          context: chatHistory.slice(-5).map(msg => ({
            role: msg.role,
            content: msg.content
          }))
        }),
      });

      if (!response.ok) {
        throw new Error(`Erreur ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      // VÃ©rifier si la rÃ©ponse contient une suggestion de parcours guidÃ©
      if (data.suggestedPath) {
        startGuidedPath(data.suggestedPath);
      } else {
        // Ajouter la rÃ©ponse de Francis avec une animation
        setChatHistory(prev => [...prev, { role: 'assistant', content: data.response }]);
      }

      // Mettre Ã  jour le profil avec les insights de la rÃ©ponse
      if (data.insights) {
        await updateProfileWithInsights(userQuestion, data.response, data.insights);
      }
    } catch (error) {
      console.error('Erreur:', error);
      // Message d'erreur plus dÃ©taillÃ© et constructif
      setChatHistory(prev => [...prev, { 
        role: 'assistant', 
        content: "Oups ! Je rencontre une difficultÃ© technique. ðŸ¤”\n\n" +
                "Voici ce que vous pouvez faire :\n" +
                "1. VÃ©rifiez votre connexion internet\n" +
                "2. RafraÃ®chissez la page\n" +
                "3. RÃ©essayez dans quelques instants\n\n" +
                "Si le problÃ¨me persiste, vous pouvez :\n" +
                "â€¢ Me reposer votre question diffÃ©remment\n" +
                "â€¢ Choisir une autre question parmi les suggestions\n" +
                "â€¢ Me contacter via le support si le problÃ¨me persiste\n\n" +
                "Je m'excuse pour ce dÃ©sagrÃ©ment et reste Ã  votre disposition ! ðŸ™"
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Fonction pour gÃ©rer le drag & drop
  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  // Fonction pour gÃ©rer le drop
  const handleDrop = async (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      await handleFileUpload(e.dataTransfer.files[0]);
    }
  };

  // Fonction pour gÃ©rer le clic sur le bouton de tÃ©lÃ©chargement
  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      await handleFileUpload(e.target.files[0]);
    }
  };

  // Fonction pour traiter le fichier
  const handleFileUpload = async (file: File | undefined, type: 'document' | 'tva' | 'frais' = 'document') => {
    if (!file) return;

    setIsUploading(true);
    const newFile = {
      name: file.name,
      type: file.type,
      status: 'processing' as const
    };

    setUploadedFiles(prev => [...prev, newFile]);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('userId', userId || '');
      formData.append('type', type);

      const response = await fetch('http://localhost:8000/process-document', {
        method: 'POST',
        body: formData,
      });

